-- Create a new database
CREATE DATABASE SQLLeadDB;

-- Create a new table
CREATE TABLE CompanyLeads (
    Id UNIQUEIDENTIFIER PRIMARY KEY,
    State INT CHECK (State BETWEEN 0 AND 3),
    CreatedDateUtc DATETIME NOT NULL,
    CancellationRequestDateUtc DATETIME NULL,
    CancellationDateUtc DATETIME NULL,
    CancellationRejectionDateUtc DATETIME NULL,
    SoldEmployee VARCHAR(255) NOT NULL,
    CancelledEmployee VARCHAR(255) NULL,
    UpdatedDateUtc DATETIME NOT NULL
);

